import React from "react";
import axios from 'axios';

export default class NewHotDogForm extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            hotdog: ""
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange = (event) => {
        this.setState({ hotdog: event.target.value });
    }

    handleSubmit = async (event) => {
        event.preventDefault();
        if (this.state.hotdog !== "") {
            console.warn('Add HotDog: ' + this.state.hotdog);
            await axios.post(`http://localhost:3001/newHotDog`, {
                hotdog: this.state.hotdog
            }).then(() => {
                this.clearForm();
                this.props.getList();
            })
        }
        // додати перевірку
    }

    clearForm = () => {
        this.setState({ hotdog: "" });
        this.props.toogleVisibilityForm();
    }

    render = () => {
        return (
            <form className="new-hotdog-form" name="form-send-hotdog" onSubmit={this.handleSubmit}>
                <input type="text" value={this.state.hotdog} onChange={this.handleChange} className="newhotdog" name="newhotdog" autoComplete="off" /><br />
                <input type="submit" className="red-button-accept form-add-buttons" value="Add"/>
                <input type="reset" onClick={this.clearForm} className="button-cancel form-add-buttons" value="Cancel"/>
            </form>
        );
    }
}
