import React from "react";
import axios from 'axios';
import NewHotDogForm from './NewHotDogForm';
import ItemList from "./ItemList";

export default class List extends React.Component {
    constructor() {
        super();
        this.state = {
            list: [],
            showForm: false,
            showList: true
        };
    }

    toogleVisibilityForm = () => {
        if (this.state.showForm) {
            this.setState({ showForm: false })
        } else {
            this.setState({ showForm: true })
        }
    }

    componentDidMount = () => {
        this.getList();
    }

    getList = async () => {
        await axios.post(`http://localhost:3001/listHotDogs`).then(response => {
            console.warn(response.data);
            this.setState({
                list: response.data.map((hotdog) => {
                    return (
                        <ItemList getList={this.getList} key={hotdog.id} element={hotdog} />
                    )
                })
            })
        })
    }

    render = () => {
        return (
            <div className="container">
                <div className="content">
                    <div className="new-hotdog-block">
                        {!this.state.showForm ? <h3 onClick={this.toogleVisibilityForm}>Add Hot Dog <button className="button-plus" >+</button></h3> : null}
                        {this.state.showForm ? <NewHotDogForm getList={this.getList} toogleVisibilityForm={this.toogleVisibilityForm} /> : null}
                    </div>
                    <h4>HotDogs</h4>
                    {this.state.showList ?
                        <ol className="hotdogs-list">
                            {this.state.list}
                        </ol>
                        : null}
                </div>
            </div>
        );
    }
}
